import axios from "axios"
import { useSelector }  from "react-redux";

const BASE_URL = "http://localhost:5000/api";
const TEMP = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyYTYyYzZiNGQwNDgzY2Q0MjFhNzEyNSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1ODg1ODI4MywiZXhwIjoxNjU5MTE3NDgzfQ.m3k7qGSlv9QWBERaD4uiXTrtVI3D0iDUMZPNC4RE1lw";
const TOKEN = JSON.parse(JSON.parse(localStorage.getItem("persist:root")).user).currentUser.accessToken;

const user = () =>{
    const user = useSelector(state=> state.user.currentUser)
}
export const publicRequest = axios.create({
    baseURL: BASE_URL
})

export const userRequest = axios.create({
    baseURL: BASE_URL,
    header: {token: user ? `Bearer ${TEMP}` : `Bearer ${TOKEN}` },
})
