import React from 'react'
import styled from "styled-components";
import { Add, Remove } from '@material-ui/icons';
import { useState } from "react";
import NavBar from '../../Navbar/NavBar';
import ScrollToTop from '../../ScrollToTop';
import Anouncement from '../../Navbar/Anouncement';
import Newsletter from '../../Newsletter/Newsletter';
import Footer from '../../Footer/Footer';
import { useEffect } from 'react';
import { publicRequest } from '../../../requestMethods';
import { useLocation } from 'react-router-dom';
import "./ProductItems.css"

const FilterColor = styled.div`
  display:flex;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props)=> props.color};
  margin: 0 5px;
  cursor: pointer;
`

const Bag = () => {
    const [value, setValue] = useState(1);
    const location = useLocation();
    const id = location.pathname.split("/")[2];
    const [product, setProduct] = useState({});

    useEffect(()=>{
        const getProducts = async () =>{
            try{
                const res = await publicRequest.get("/products/find/" + id);
                setProduct(res.data);
            }catch(err){
                console.log(err);
            }
        }
        getProducts();
    }, [id])

    const decreaseValue = ()=>{
        if(value > 1){
            setValue(value - 1)
        }
    }
  return (
    <>
      <ScrollToTop/>
      <Anouncement/>
      <NavBar/>
        <div className = "productItemContainer">
            <div className = "productImageContainer">
                <img src = {product.img} alt = "jeans" id = "productItemImage"/>
            </div>
    
            <div className = "productInfoContainer">
                <h1 id = "productItemTitle">Title</h1>
                <p id = "productItemDescription">is simply dummy text of the printing and typesetting industry. 
                Lorem Ipsum has been the industry's standard dummy text ever
                    since the 1500s, when an unknown printer took a galley of 
                    type and scrambled it to make a type specimen book. 
                It has survived not only five centuries</p>
                <p id = "productItemPrice">$20</p>
    
            <div className = "productItemFilterContainer">
                <div className = "colorFilter">
                    <h4 id = "productItemColor">Color</h4>
                    <FilterColor color = "black" value = "black"/>
                    <FilterColor color = "blue" value = "blue"/>
                    <FilterColor color = "grey" value = "grey"/>
                </div>
    
                <div className = "sizeFilter"> 
                    <h4 id = "productItemSize">Size</h4>
                    <select className = "sizeSelector">
                        <option>XS</option>
                        <option>S</option>
                        <option>M</option>
                        <option>L</option>
                    </select>
                </div>
            </div>
    
                <div className = "addContainer">
                    <span id = "Amount">
                    <Remove id = "remove" onClick = {decreaseValue}/>
                    {value}
                    <Add id = "add" onClick= {()=>setValue(value+1)}/>
                    </span>
                    <button id = "cartAdd">
                    Add to Cart
                    </button>
                </div>
    
            </div>
        </div>
    <Newsletter/>
    <Footer/>
    </>
  )
}

export default Bag
