import React from 'react'
import { products } from '../../../data'
import "./Products.css"
import { Link } from 'react-router-dom';
import NavBar from '../../Navbar/NavBar';
import ScrollToTop from '../../ScrollToTop';
import Anouncement from '../../Navbar/Anouncement';
import Newsletter from '../../Newsletter/Newsletter';
import Footer from '../../Footer/Footer';
import { useLocation } from 'react-router-dom';
import { useState } from "react";
import { useEffect } from 'react';
import axios from "axios";


const Products = () => {

  const location = useLocation();
  const cat = location.pathname.split("/")[2];
  const [filter, setFilter] = useState({});
  const [sort, setSort] = useState("newest");
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(()=>{
    const getProducts = async () =>{
      try{
        const res = await axios.get("http://localhost:5000/api/products")
        console.log(res);
      }catch(err){

      }
    }
  },[cat]);

  const handleFilter = (e)=>{
    const value = e.target.value;
    setFilter({
      ...filter,
      [e.target.name] : value
    });
  }

  const handleSort = (e) =>{
    setSort(e.target.value);
  }

  return (
    <div className = "container">
      <ScrollToTop/>
      <Anouncement/>
      <NavBar/>
      <h1 id = "productHeading">Products</h1>
      <div className = "filterSection">

          <div className = "filterProducts">
          <h3 id = "filter">Filter Products: </h3>
            <select id = "colorSelect" name = "color" onChange = {handleFilter}>
              <option disabled>color</option>
              <option>black</option>
              <option>white</option>
              <option>yellow</option>
              <option>other</option>
            </select>
            <select id = "sizeSelect" name = "size" onChange = {handleFilter}>
              <option disabled>Size</option>
              <option>S</option>
              <option>M</option>
              <option>L</option>
              <option>XL</option>
            </select>
          </div>

          <div className = "sortProducts">
            <h3 id = "sort">Sort Products: </h3>
            <select id = "productSelect" onChange = {handleSort}>
              <option value = "newest"> Newest </option>
              <option value = "asc"> Price (low to high) </option>
              <option value = "desc"> Price (high to low) </option>
            </select>
          </div>
          
      </div>
      <div className = "productContainer">
        {products.map(item =>(
          <Link to = {"/product/" + item.component}>
          <div className = "imageContainer">   
            <img src = {item.img} key = {item.id} alt = "none" id = "productImage"/>
          </div>
          </Link>
        ))}
      </div>
      <Newsletter/>
      <Footer/>
    </div>

  )
}

export default Products
